import { app, BrowserWindow, ipcMain } from "electron";
import {
  createOverlaySettingsFile,
  createOverlaysFolder,
  getOverlayNames,
} from "./utils/overlaysFolder";
import { gamesWebSocketServerAPI, irsdkipc } from "./games-api";
import { createOverlayWindow } from "./utils/createOverlayWindow";
import path from "path";
import { OVERLAYS_PATH } from "./constants";
import overlayServer from "./overlay-server";
import { OVERLAY_SERVER_PORT } from "../shared/constants";
// This allows TypeScript to pick up the magic constants that's auto-generated by Forge's Webpack
// plugin that tells the Electron app where to look for the Webpack-bundled app code (depending on
// whether you're running in development or production).
declare const MAIN_WINDOW_WEBPACK_ENTRY: string;
declare const MAIN_WINDOW_PRELOAD_WEBPACK_ENTRY: string;

const windows: BrowserWindow[] = [];

// Handle creating/removing shortcuts on Windows when installing/uninstalling.
if (require("electron-squirrel-startup")) {
  app.quit();
}

createOverlaysFolder();
createOverlaySettingsFile();
overlayServer.listen(OVERLAY_SERVER_PORT, "localhost", () => {
  console.log("Overlay server is listening at port: " + OVERLAY_SERVER_PORT);
});

const createWindow = (): BrowserWindow => {
  // Create the browser window.
  const mainWindow = new BrowserWindow({
    height: 800,
    width: 1200,
    webPreferences: {
      preload: MAIN_WINDOW_PRELOAD_WEBPACK_ENTRY,
      nodeIntegration: true,
    },
    frame: false,
  });

  mainWindow.webContents.setAudioMuted(true);

  // Open the DevTools.
  mainWindow.webContents.openDevTools();

  // and load the index.html of the app.
  mainWindow.loadURL(MAIN_WINDOW_WEBPACK_ENTRY);

  irsdkipc.open();
  gamesWebSocketServerAPI.setSelectedGame("IRACING");

  mainWindow.on("close", () => {
    windows.forEach((win) => {
      win.removeAllListeners("close"); // Remove the close prevention listener
      win.close(); // Close the overlay window
    });

    if (irsdkipc) {
      irsdkipc.removeAllListeners();
      irsdkipc.kill(); // Ensuring child process is terminated
    }
    app.quit();
  });

  ipcMain.on("main-message", (_, data) => {
    if (data === "switch-to-iracing") {
      irsdkipc.open();
      gamesWebSocketServerAPI.setSelectedGame("IRACING");
    }
    // TODO: Remove/Replace "disconnect-from-iracing"
    if (data === "disconnect-from-iracing") {
      irsdkipc.kill();
      gamesWebSocketServerAPI.setSelectedGame("NONE");
    }

    if (data === "get-mod-names") {
      getOverlayNames();
    }
  });

  ipcMain.on("title-bar-message", (_, data) => {
    if (data === "close") {
      mainWindow.close();
    } else if (data === "minimize") {
      mainWindow.minimize();
    }
  });

  return mainWindow;
};

// This method will be called when Electron has finished
// initialization and is ready to create browser windows.
// Some APIs can only be used after this event occurs.
app.on("ready", () => {
  windows.push(createWindow());

  const modFolders = getOverlayNames();

  for (let i = 0; i < modFolders.length; i++) {
    windows.push(
      createOverlayWindow(
        path.join(OVERLAYS_PATH, modFolders[i], "index.html"),
        {
          width: 500,
          height: 200,
        }
      )
    );

    /* Getting an overlay from dev server */

    // windows.push(
    //   createOverlayWindow("http://localhost:5173", {
    //     width: 500,
    //     height: 200,
    //   })
    // );
  }
});

// Quit when all windows are closed, except on macOS. There, it's common
// for applications and their menu bar to stay active until the user quits
// explicitly with Cmd + Q.
app.on("window-all-closed", () => {
  if (process.platform !== "darwin") {
    app.quit();
  }
});

app.on("activate", () => {
  // On OS X it's common to re-create a window in the app when the
  // dock icon is clicked and there are no other windows open.
  if (BrowserWindow.getAllWindows().length === 0) {
    createWindow();
  }
});

